import React, { useState } from 'react';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import KeyboardArrowRightIcon from '@material-ui/icons/KeyboardArrowRight';
import {FormControlLabel, makeStyles} from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import Radio from '@material-ui/core/Radio'
import RadioGroup from '@material-ui/core/RadioGroup'
import  FormLabel from '@material-ui/core/FormLabel'
import  FormControl from '@material-ui/core/FormControl'
import { useHistory } from 'react-router';

const useStyles=makeStyles({
  field:{
    marginTop:20,
    marginBottom:20,
    display:'block'
  }
})

export default function Create() {

  const classes=useStyles()
  const history=useHistory()
  const [title, setTitle] = useState('');
  const [details, setDetails] = useState('');
  const [titleError, setTitleError] = useState(false);
  const [detailsError, setDetailsError] = useState(false);
  const [category, setCategory] = useState('todos');

  const handleSubmit=(e)=>{
    e.preventDefault()
    setTitleError(false)
    setDetailsError(false)
    if(title==''){
      setTitleError(true)
    }
    if(details==''){
      setDetailsError(true)
    }
    if(title && details){
      etch('http://localhost:8000/notes',{
        method:'POST',
        headers:{'Content-type': 'application/json'},
        body:JSON.stringify({title,details,category})
      }).then(()=> history.push('/'))
    }
  }
  return (
    <Container>
      <Typography
        className={classes.title}
        variant='h6'
        component='h2'
        gutterBottom
        color='textSecondary'
      >
        Create a New Note
      </Typography>

      <form noValidate autoComplete="off" onSubmit={handleSubmit}>
        <TextField
                  onChange={(e)=>{setTitle(e.target.value)}}
                  className={classes.field} 
                  label='Note Title'  
                  variant='outlined'
                  color='secondary'
                  fullWidth
                  required
                  error={titleError}
        />
        <TextField
                  onChange={(e)=>{setDetails(e.target.value)}}
                  className={classes.field} 
                  label='Details'  
                  variant='outlined'
                  color='secondary'
                  fullWidth
                  multiline
                  rows={4}
                  required
                  error={detailsError}
        />

      <FormControl className={classes.field}>
      <FormLabel>Note Category</FormLabel>
      <RadioGroup value={category} onChange={(e)=>{setCategory(e.target.value)}}>
        <FormControlLabel control={<Radio/>} label='Money' value='money'/>  
        <FormControlLabel control={<Radio/>}label='Todos' value='todos'/>  
        <FormControlLabel control={<Radio/>} label='Reminders' value='reminders'/>   
        <FormControlLabel control={<Radio/>} label='Work' value='work'/>  
      </RadioGroup>
      </FormControl>

      <Button
        className={classes.btn}
        type='submit'
        color='secondary'
        variant='contained'
        endIcon={<KeyboardArrowRightIcon/>}
      >
        Submit
      </Button>

      </form>


    </Container>


      // {/* <Button type='submit'>Submit</Button>
      // <Button type='submit' color='secondary' variant='outlined'>Submit</Button>

      // <ButtonGroup color='primary' variant='contained'>
      //   <Button>one</Button>
      //   <Button>two</Button>
      //   <Button>three</Button>
      // </ButtonGroup> */}
    //<Typography 
    //     variant='h1'
    //     color='primary'
    //     align='center'
    //   >
    //     Create New Note
    //   </Typography>
    //   <Typography 
    //     noWrap
    //     color='secondary'
    //   >
    //     Lorem ipsum dolor sit, amet consectetur adipisicing elit. Soluta qui quidem facere at porro, natus nihil, molestiae repudiandae ab quia rerum fuga. Culpa temporibus quae, at veniam numquam dignissimos unde!
    //   </Typography>

  )
}
